mapper.update:
UPDATE CONTENT
SET 
	TITLE = #{title},
    TEXT = #{text, jdbcType=CLOB}, 
    LAST_UPDATED = #{lastUpdated}
WHERE CONTENT_ID = #{contentId}

    	UPDATE CONTENT
		SET RECOMMEND = RECOMMEND + 1
		WHERE CONTENT_ID = #{contentId}

mapper.select:
  		SELECT RECOMMEND
  		FROM CONTENT
	    WHERE CONTENT_ID = #{contentId}
