mapper.select:
SELECT content_id AS contentId, user_id AS userId, title, date_created AS dateCreated, last_updated AS lastUpdated, file_id AS fileId, recommend AS recommendCnt, text
FROM Content
WHERE user_id = #{userId}

SELECT comment_id AS commentId, user_id AS userId, content_id AS contentId, text, date_created AS dateCreated
FROM PostComment
WHERE user_id = #{userId}
